import React from 'react'
import { useState } from 'react';
import '../App.css'
import { useEffect } from 'react';

export default function EditAssumedIngredient(props) {
  console.log(props);
  const unit = props.ingredient.unit;
  console.log(unit);
  const [selectedQuantity, setSelectedQuantity] = useState(0);
  const [ingredient, setIngredient] = useState(props.ingredient);

    useEffect(() => {
    const unit = props.ingredient.unit;
    if (['lb', 'kg', 'g'].includes(unit)) {
        setSelectedQuantity(1);
    } else if (['milliliters', 'liters', 'cups', 'tbsp', 'tsp'].includes(unit)) {
        setSelectedQuantity(2);
    } else if (['small', 'medium', 'large'].includes(unit)) {
        setSelectedQuantity(3);
    } else {
        setSelectedQuantity(0);
    }
    }, [props.ingredient.unit]); // Run this whenever the unit changesÂ 

  let handleQuantityClassification = (e) => {
    console.log(e.target.value)
    if(e.target.value === 'none') setSelectedQuantity(0)
    else if(e.target.value === 'weight') setSelectedQuantity(1)
    else if(e.target.value === 'volume') setSelectedQuantity(2)
    Â else if(e.target.value === 'general') setSelectedQuantity(3)
  }

//   let selectQuantityClassification = e => {

//     if(selectedQuantityInt == 2) e.target.value = 'weight'
//   }
  return (
    <div style={props.style}>
        <h1>Edit Recipe</h1>
        <input defaultValue={props.ingredient.assumedQuantity} type='number' onChange={(e) => {
            let copyIngredient = {...ingredient};
            copyIngredient.assumedQuantity = e.target.value;
            setIngredient(copyIngredient);

        }} />
        <div className='formFlex'>
            <select value={selectedQuantity == 0 ? 'none' : selectedQuantity === 1 ? 'weight' : selectedQuantity === 2 ? 'volume' : 'general'} id='quantityClassification' name='quantityClassification' onChange={(e) => handleQuantityClassification(e)}>
                <option value="none">None</option>
                <option value="weight">Weight</option>
                <option value="volume">Volume</option>
                <option value="general">General</option>
            </select>
                <select value={props.ingredient.unit} id='weightQuantities' name='weightQuantities' style={{'display':selectedQuantity === 1 ? 'block' : 'none'}} onChange={e => {
                    let copyIngredient = {...ingredient}

                }}>
                    <option value="kg">kg</option>
                    <option value="lb">lb</option>
                    <option value="g">g</option>
                </select>

                <select value={props.ingredient.unit} id='volumeQuantities' name='volumeQuantities' style={{'display':selectedQuantity === 2 ? 'block' : 'none'}} onChange={e => {
                    let copyIngredient = {...ingredient}

                }}>
                    <option value="milliliters">milliliters</option>
                    <option value="liters">liters</option>
                    <option value="cups">cups</option>
                    <option value="tbsp">tbsp</option>
                    <option value="tsp">tsp</option>
                </select>

                <select value={props.ingredient.unit} id='generalQuantities' name='generalQuantities' style={{'display':selectedQuantity === 3 ? 'block' : 'none'}} onChange={e => {
                    let copyIngredient = {...ingredient}

                }}>
                    <option value="small">small</option>
                    <option value="medium">medium</option>
                    <option value="large">large</option>
                </select>
        </div>
        <input value={props.ingredient.ingredientName} />
        <button onClick={() =>{ 
            let quantity = document.getElementById('quantityClassification').value
            let weightQuantities = document.getElementById('weightQuantities').value
            let volume = document.getElementById("volumeQuantities").value
            let genQuantities = document.getElementById('generalQuantities').value;

            if (quantity == 'none') {
                props.setItemToEdit({ingredientName: props.ingredientName, assumedQuantity: quantity, unit: ''})
            } 
            else if (quantity=='weight') {
                props.setItemToEdit({ingredientName: props.ingredientName, assumedQuantity: quantity, unit: weightQuantities})

            }

            else if (quantity=='volume') {
                props.setItemToEdit({ingredientName: props.ingredientName, assumedQuantity: quantity, unit: volume})

            }

            else if (quantity=='general') {
                props.setItemToEdit({ingredientName: props.ingredientName, assumedQuantity: quantity, unit: genQuantities})

            }

        }}>Submit</button>
    </div>
  )
}
