import React from 'react'
import { useState } from 'react'
import RecipeView from "./RecipeView";
import SelectedRecipe from "./SelectedRecipe"
import Error from './Error';

export default function Main() {

    let [ingredients, setIngredients] = useState("");
    let [numRecipes, setNumRecipes] = useState(0);
    let [recipes, setRecipes] = useState([])
    let [loading, setLoading] = useState(false);
    let [experience, setExperience] = useState("beginner");
    let [selectedRecipe, setSelectedRecipe] = useState(null);
    let [selectedRecipeIndex, setSelectedRecipeIndex] = useState(-1);
    let [message, setMessage] = useState(null)

    let fetchRecipes = () => {
        if (ingredients == "" || numRecipes < 1) {
            setMessage("The ingredients that were provided are not valid.");
        } else {
            setLoading(true);
            fetch("http://localhost:8000/get-recipes", {
                method: "POST",
                body: JSON.stringify({
                    ingredients: ingredients,
                    numRecipes: numRecipes,
                    experienceLevel: experience
                }),
                headers: {
                    'Content-Type': "application/json"
                }
            })
                .then(response => {
                    if (response.status === 200)
                        return response.json()
                    else setMessage("An internal server occured, try again.")
                })
                .then(data => {
                    console.log(data)
                    if(data.message === None) {
                        setLoading(false);
                        setRecipes(data)
                    } else {
                        setLoading(false);
                        setMessage(data.message)
                    }


                })
        }
    };

    return message !== null ? <Error message={message} /> : selectedRecipe ? <SelectedRecipe modifyRecipes={setRecipes} recipeList={recipes} recipe={selectedRecipe} setState={setSelectedRecipe} index={selectedRecipeIndex} /> : (
        <div className="container">
            <div className="header">
                <h1>michelinGPT</h1>
                <p>Enter at least three ingredients to generate possible recipes.</p>
            </div>
            <div className="body">
                <div class="form">
                    <input className="ingredients" name="ingredients" placeholder="Ingredients" onChange={e => setIngredients(e.target.value)} />
                    <input className="numRecipes" name="numRecipes" placeholder="# of Recipes" type='number' onChange={e => setNumRecipes(e.target.value)} />
                    <select id='experience' name='experience' onChange={e => setExperience(e.target.value.toLowerCase())}>
                        <option value="beginner">Beginner</option>
                        <option value="intermediate">Intermediate</option>
                        <option value="advanced">Advanced</option>
                    </select>
                    <button className="submit" onClick={e => fetchRecipes()}>Submit</button>
                </div>
                <p style={{ "display": loading ? "block" : "none" }}>Your recipes are being generated behind the scenes. Please wait.</p>
            </div>
            <div className="gap"></div>
            <div className="recipes-container" style={{ 'display': loading ? 'none' : 'block' }}>
                {recipes != null ? recipes.map((recipe_subarray, i) => {
                    return (<div className="recipes">
                        {recipe_subarray.map((recipe, j) => {
                            return <RecipeView recipe={recipe} setState={setSelectedRecipe} key={(i * 3) + j} modifyRecipes={setRecipes} setIndex={setSelectedRecipeIndex} recipeIndex={(i * 3) + j} />
                        })}
                    </div>)
                }) : []}
            </div>
        </div>
    )
}
