import React, { use } from 'react'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faEdit, faTrash } from '@fortawesome/free-solid-svg-icons'
import { useState } from 'react'
import EditAssumedIngredient from './EditAssumedIngredient'

export default function AssumedIngredient(props) {
    let ingInd = props.ingredientIndex;
    let ingredient = props.ingredientInfo
    let ingredientList = props.ingredientList;
    let name = ingredient.ingredient
    let quantity = ingredient.quantity;
    let unit = ingredient.unit;

    let changeIngredient = props.changeAssumedIngredients

    let [editForm, setEditForm] = useState(false)
    // let [edited, setEdited] = useState(false);
    let deleted = ingredient.removed
    let edited = ingredient.edited
    // let deleted = ingredient.delete


    function handleEdit() {
        setEditForm(true);

    }

    return editForm ? (<EditAssumedIngredient setItemToEdit={setEditForm} setEditForm={setEditForm} ingredients={props.ingredientList} ingredient={ingredient} index={ingInd} changeIngredient={changeIngredient} />) : (
        <div>
            <span className="assumedIngredient">
                <p style={{'textDecoration':deleted ? 'line-through' : 'none'}}>{quantity} {unit} {name}</p>
                <FontAwesomeIcon className="icon" icon={faEdit} onClick={handleEdit} />
                <FontAwesomeIcon className="icon" icon={faTrash} onClick={() => {
                    let newIng = [...ingredientList]
                    let updated = {
                        ...newIng[ingInd],
                        removed: !deleted
                    }
                    newIng[ingInd] = updated;
                    props.changeAssumedIngredients(newIng)
                    }} />
            </span>
        </div>
    )
}
