import React from 'react'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faEdit, faTrash } from '@fortawesome/free-solid-svg-icons'
import RecipeHeader from './RecipeHeader';
import AssumedIngredient from './AssumedIngredient';
import { useState } from 'react';

export default function SelectedRecipe(props) {
  let [recipe, setRecipe] = useState(props.recipe);

  let assumedIngredients = recipe.assumed_ingredients
  assumedIngredientsMap = assumedIngredients.map((ingredient_name, i) => { return { "ingredient": ingredient_name, "quantity": recipe.assumed_quantities[i], "unit": recipe.assumed_quantities_unit[i], "edited": false, "removed": false } })
  let [assumedIngredientsState, setAssumedIngredients] = useState(assumedIngredientsMap)



  return (
    <div className="container">

      <div className="selectedRecipeContainer">

        <button id="backBtn" onClick={() => props.setState(null)}>←</button>
        <RecipeHeader recipe={recipe} />
        <div className="ingredientsAndSteps">
          <div className="steps">
            <h3>Steps to Cook</h3>
            {recipe.steps.map(step => {
              return <p>{step}</p>
            })}
          </div>
          <div className="assumedIngredients">
            <div className="ingredients">
              <h3>Your Ingredients</h3>
              {recipe.users_ingredients.map(step => {
                return <p>{step}</p>
              })}
            </div>
            <div className="assumed">
              <h3>Assumed Ingredients</h3>

              {assumedIngredientsState.map((step, i) => {

                return (
                  <AssumedIngredient ingredientList={assumedIngredientsState} ingredientInfo={step} changeAssumedIngredients={setAssumedIngredients} ingredientIndex={i} />
                )
              })}
              <button onClick={() => {
                let editedItems = [];
                let changedItems = [];
                let deletedItems = [];

                for (let i = 0; i < assumedIngredientsState.length; i++) {
                  if (assumedIngredientsState[i].edited) {
                    let str = assumedIngredientsState[i].quantity + ' ' + assumedIngredientsState[i].unit + ' ' + assumedIngredientsState[i].ingredient
                    editedItems.push(str)
                    let origIng = assumedIngredients.assumed_ingredients[i] + ' ' + assumedIngredients.assumed_quantities[i] + ' ' + assumedIngredients.assumed_quantities_unit[i]
                    changedItems.push(origIng)
                  }
                  else if(assumedIngredientsState[i].deleted) {
                    let str = assumedIngredientsState[i].quantity + ' ' + assumedIngredientsState[i].unit + ' ' + assumedIngredientsState[i].ingredient
                    deletedItems.push(str)
                  }
                  fetch('http://localhost:8000/edit-recipe', {
                    method: "POST", headers: {
                        'Content-Type': "application/json"
                    }, body: JSON.stringify({ 'recipe': recipe, 'excluded_ingredients': deletedItems, "edited_items": editedItems, "changed_items":changedItems })
                })
                    .then(response => {
                        return response.json()
                    })
                    .then(data => {
                        console.log(data);
                    })
                }
              }}>Remove Item(s)</button>
            </div>
          </div>
        </div>
      </div>
    </div>
  )
}
