import React from 'react'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faEdit, faTrash } from '@fortawesome/free-solid-svg-icons'

export default function SelectedRecipe(props) {
  let recipe = props.recipe;
  console.log(recipe)
  let modifyRecipes = props.modifyRecipes;
  let recipeList = props.recipeList;
  let setSelectedRecipe = props.setSelectedRecipe;
  let index = props.index;

  return (
    <div className="container">

      <div className="selectedRecipeContainer">

        <button id="backBtn" onClick={() => props.setState(null)}>‚Üê</button>
        <RecipeHeader recipe={recipe} />
        <div className="ingredientsAndSteps">
          <div className="steps">
            <h3>Steps to Cook</h3>
            {recipe.steps.map(step => {
              return <p>{step}</p>
            })}
          </div>
          <div className="assumedIngredients">
            <div className="ingredients">
              <h3>Your Ingredients</h3>
              {recipe.users_ingredients.map(step => {
                return <p>{step}</p>
              })}
            </div>
            <div className="assumed">
              <h3>Assumed Ingredients</h3>
              {recipe.assumed_ingredients.map((step, i) => {
                return (
                  <span class="assumedIngredient">
                    <p>{step}</p>
                    <FontAwesomeIcon className="icon" icon={faEdit} /> <FontAwesomeIcon className="icon" icon={faTrash} />
                  </span>
                )
              })}
              <button>Remove Item(s)</button>
            </div>
          </div>
        </div>
      </div>
    </div>
  )
}
