import React from 'react'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faEdit, faTrash } from '@fortawesome/free-solid-svg-icons'
import { useState } from 'react'


export default function SelectedRecipe(props) {
    let recipe = props.recipe

    let [ removedItems, setRemovedItems ] = useState([]);
    let [ editItems, setEditedItems ] = useState(Array(recipe.assumed_ingredients).fill(false));
    return (
        <div className="container">
            <button id="backBtn" onClick={() => props.setState(null)}>‚Üê</button>
            <h1>{recipe.title}</h1>
            <p>{recipe.description}</p>
            <p>Cooking ETA: <span className="bold">{recipe.eta}</span></p>
            <p>CALORIES: {recipe.calories} cal | CARBS: {recipe.carbs}g | FAT: {recipe.fat}g | PROTEIN: {recipe.protein}g</p>
            <div className="ingredientsAndSteps">
                <div className="steps">
                    <h3>Steps to Cook</h3>
                    {recipe.steps.map(step => {
                        return <p>{step}</p>
                    })}
                </div>
                <div className="assumedIngredients">
                    <div className="ingredients">
                        <h3>Your Ingredients</h3>
                        {recipe.users_ingredients.map(step => {
                            return <p>{step}</p>
                        })}
                    </div>
                    <div className="assumed">
                        <h3>Assumed Ingredients</h3>
                        {recipe.assumed_ingredients.map((step, i) => {
                            return <span class="assumedIngredient"><p>{step} <FontAwesomeIcon className="icon" icon={faEdit}  /> <FontAwesomeIcon className="icon" icon={faTrash} onClick={() => {
                                removedItems[i] = !removedItems[i]
                                setRemovedItems(editItems);
                            }} style={{'textDecoration': removedItems[i] ? 'line-through' : 'none'}} /> </p></span>
                        })}
                        <button style={{"display": removedItems.filter(recipe => recipe.assumed_ingredients).length > 0 ? 'block' : 'none'}}>Remove Item(s)</button>
                    </div>
                </div>
            </div>
        </div>
    )
}
