import React from 'react'
import { useState } from 'react';
import '../App.css'

export default function EditAssumedIngredient(props) {
  let [ selectedQuantity, setSelectedQuantity ] = useState(0);
  let handleQuantityClassification = (e) => {
    console.log(e.target.value)
    if(e.target.value == 'None') setSelectedQuantity(0)
    else if(e.target.value == 'weight') setSelectedQuantity(1)
    else if(e.target.value == 'Volume') setSelectedQuantity(2)
     else if(e.target.value == 'General') setSelectedQuantity(3)
  }
  return (
    <div style={props.style}>
        <h1>Edit Recipe</h1>
        <input value={props.quantity} type='number' />
        <div className='formFlex'>
            <select id='quantityClassification' name='quantityClassification' onChange={(e) => handleQuantityClassification(e)}>
                <option value="none">None</option>
                <option value="weight">Weight</option>
                <option value="volume">Volume</option>
                <option value="general">General</option>
            </select>
            <select id='weightQuantities' name='weightQuantities' style={{'display':selectedQuantity == 1 ? 'block' : 'hidden'}}>
                <option value="kilograms">kg</option>
                <option value="pounds">lb</option>
                <option value="grams">g</option>
            </select>
            <select id='volumeQuantities' name='volumeQuantities' style={{'display':selectedQuantity == 2 ? 'block' : 'hidden'}}>
                <option value="ml">millileters</option>
                <option value="l">liters</option>
                <option value="cups">cups</option>
                <option value="tbsp">tbsp</option>
                <option value="tsp">tsp</option>
            </select>
            <select id='generalQuantities' name='generalQuantities' style={{'display':selectedQuantity == 3 ? 'block' : 'hidden'}}>
                <option value="small">small</option>
                <option value="medium">medium</option>
                <option value="large">large</option>
            </select>
        </div>
        <input value={props.unit} />
        <input value={props.ingredientName} />
        <button onClick={() =>{ 
            let quantity = document.getElementById('quantityClassification').value
            let weightQuantities = document.getElementById('weightQuantities').value
            let volume = document.getElementById("volumeQuantities").value
            let genQuantities = document.getElementById('generalQuantities').value;

            if (quantity == 'none') {
                props.setItemToEdit({ingredientName: props.ingredientName, assumedQuantity: quantity, unit: ''})
            } 
            else if (quantity=='weight') {
                props.setItemToEdit({ingredientName: props.ingredientName, assumedQuantity: quantity, unit: weightQuantities})

            }

            else if (quantity=='volume') {
                props.setItemToEdit({ingredientName: props.ingredientName, assumedQuantity: quantity, unit: volume})

            }

            else if (quantity=='general') {
                props.setItemToEdit({ingredientName: props.ingredientName, assumedQuantity: quantity, unit: genQuantities})

            }

        }}>Submit</button>
    </div>
  )
}
