import React, { use } from 'react'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faEdit, faTrash } from '@fortawesome/free-solid-svg-icons'
import { useState } from 'react'
import EditAssumedIngredient from './EditAssumedIngredient'

export default function AssumedIngredient(props) {
    let ingredient = props.ingredient
    let name = ingredient.name
    let quantity = ingredient.quantity;
    let unit = ingredient.unit;

    let [editForm, setEditForm] = useState(false)

    // const handleEdit = () => {
    //     console.log("dikc adnd ball")
    // }

    function handleEdit() {
        setEditForm(true);

    }

    return editForm ? (<EditAssumedIngredient ingredient={ingredient} />) : (
        <div>

            <h3>Assumed Ingredients</h3>
            <span className="assumedIngredient">
                <p>{name}</p>
                <FontAwesomeIcon className="icon" icon={faEdit} onClick={handleEdit} />
                <FontAwesomeIcon className="icon" icon={faTrash} onClick={() => { }} />
            </span>
            <button>Remove Item(s)</button>
        </div>
    )
}
