import React from 'react'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faEdit, faTrash } from '@fortawesome/free-solid-svg-icons'
import RecipeHeader from './RecipeHeader';
import AssumedIngredient from './AssumedIngredient';
import { useState } from 'react';

export default function SelectedRecipe(props) {
  console.log(props)
  let recipe = props.recipe;
  let assumedIngredients = recipe.assumed_ingredients
  assumedIngredients = assumedIngredients.map((ingredient_name, i) => {return {"ingredient": ingredient_name, "quantity": recipe.assumed_quantities[i], "unit":recipe.assumed_quantities_unit[i], "edited":false, "removed":false}})
  let [assumedIngredientsState, setAssumedIngredients] = useState(assumedIngredients)

  return (
    <div className="container">

      <div className="selectedRecipeContainer">

        <button id="backBtn" onClick={() => props.setState(null)}>‚Üê</button>
        <RecipeHeader recipe={recipe} />
        <div className="ingredientsAndSteps">
          <div className="steps">
            <h3>Steps to Cook</h3>
            {recipe.steps.map(step => {
              return <p>{step}</p>
            })}
          </div>
          <div className="assumedIngredients">
            <div className="ingredients">
              <h3>Your Ingredients</h3>
              {recipe.users_ingredients.map(step => {
                return <p>{step}</p>
              })}
            </div>
            <div className="assumed">
              <h3>Assumed Ingredients</h3>

              {assumedIngredientsState.map((step, i) => {

                return (
                  <AssumedIngredient ingredientList={assumedIngredientsState} ingredientInfo={step} changeAssumedIngredients={setAssumedIngredients} ingredientIndex={i} />
                )
              })}
              <button onClick={() => {
                console.log()
              }}>Remove Item(s)</button>
            </div>
          </div>
        </div>
      </div>
    </div>
  )
}
