import React from 'react'
import { useState } from 'react';
import '../App.css'
import { useEffect } from 'react';

export default function EditAssumedIngredient(props) {
    // let ingredient = props.ingredient
    let [ingredient, setIngredient] = useState(props.ingredient)
    let [quantity, setQuantity] = useState(ingredient.assumedQuantity);
    // let [quantityCategory, setQuantityCategory] = useState("none");
    let [unit, setUnit] = useState(ingredient.unit)
    let [ingredientName, setIngredientName] = useState(ingredient.ingredientName)

    return (
        <div class='container'>
            <a onClick={e => props.setItemToEdit(null)}>Exit</a>
            <h1>Edit Ingredient</h1>
            <input defaultValue={quantity} type='number' onChange={(e) => {
                setQuantity(e.target.value);
                }} />
            <div className='formFlex'>
                <select value={unit} onChange={e => {
                    setUnit(e.target.value)
                }}>
                    <option value="kg">kg</option>
                    <option value="lb">lb</option>
                    <option value="g">g</option>
                    <option value="mL">milliliters</option>
                    <option value="L">liters</option>
                    <option value="cup">cups</option>
                    <option value="tbsp">tbsp</option>
                    <option value="tsp">tsp</option>
                    <option value="small">small</option>
                    <option value="medium">medium</option>
                    <option value="large">large</option>
                    <option value='clove'>cloves</option>
                    <option value='piece'>pieces</option>
                    <option value='slice'>slices</option>
                    <option value='leaf'>leaves</option>
                    <option value='sprig'>sprigs</option>
                    <option value='stalk'>stalks</option>
                    <option value='inch'>inch</option>
                    <option value='head'>heads</option>
                    <option value='strip'>strips</option>
                    <option value='pinch'>pinch</option>
                </select>
            </div>

            <input value={ingredientName} onChange={e => setIngredientName(e.target.value)} />
            <button onClick={e => {
                console.log(props.recipe)
                let copyRender = [...props.itemsRender]
                copyRender[props.ingredientIndex] = `${quantity} ${unit} ${ingredientName}`
                console.log(copyRender[props.ingredientIndex])
                props.updateItems(copyRender);
                props.setItemToEdit(null);
            }}>Submit</button>
        </div>
    )
}
